document.addEventListener("DOMContentLoaded",()=>{function t(t,o){const e=o?-6:0;t.style.transform=`rotate(${t.randomRotation}deg) translate(${t.posX}px, ${t.posY+e}px)`}document.querySelectorAll(".bit-card").forEach(o=>{o.randomRotation=2*(Math.random()-.5),o.posX=8*(Math.random()-.5),o.posY=8*(Math.random()-.5),t(o,!1),o.addEventListener("mouseenter",()=>{t(o,!0)}),o.addEventListener("mouseleave",()=>{o.randomRotation=2*(Math.random()-.5),t(o,!1)})})});