document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("a").forEach(t=>{const e=t.getAttribute("href");!e||e.startsWith("http")||e.startsWith("#")||"_blank"===t.target||t.addEventListener("click",t=>{t.preventDefault(),document.body.classList.add("page-exit"),setTimeout(()=>window.location.href=e,250)})})});